    const response = await fetch('/gen',postOptions);
    const body = await response.json();

    if (response.status !== 200) throw Error(body.message);

    return body;

  }

  checkGameId = async () => {
    // asks the server if the join game id is valid

    const postOptions = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({gameId:this.state.joinGameId})
    };

    const response = await fetch('/checkGameId',postOptions);
    const body = await response.json();

    if (response.status !== 200) throw Error(body.message);

    return body.isValidGame;

  }

  componentDidMount() {
    this.callAPI()
      // .then(res => console.log(res.express))
      .then(res => this.setState({apiResponse: res.express}))
      .catch(err => console.log(err));

    // this.getGameId()
    // .then(res => this.setState({gameId: res.id}))
    // .catch(err => console.log(err));

    subToTimer((err,timestamp) => this.setState({timestamp:timestamp}));
    subToToggle(this.state.toggle,(err,gameId,toggle) => {
      if (gameId===this.state.gameId) {
        console.log(toggle);
        this.setState({toggle: (toggle==='False') ? 'True' : 'False'});
      }
    });



    this.handleChange = this.handleChange.bind(this);
    // this.makeMove = this.makeMove.bind(this);
  }


//onMove={() => this.makeMove()}
  render() {
    let connected;
    if (this.state.gameId!=='') {
      connected = <div>
        <Board gameId={this.state.gameId} player={this.state.player} joinGameId={this.state.joinGameId}  />
        <p>Your game id is: {this.state.gameId}</p>
        <p>Have player join using that game id</p>
        <button onClick={() => this.disconnect()}>Disconnect</button>
      </div>;
    }
    else {
      connected = <div>
        <p>Create or join a room to play!</p>
        <button onClick={() => this.createRoom()}>Create Room</button>
        <p> or </p>
        <button onClick={() => this.joinRoom()}>Join Room</button>
          <input type="text" placeholder="Game id" value={this.state.joinGameId} onChange={this.handleChange} />
      </div>;
    }
    return (
        <div className="Game">
          <p>Api Response: {this.state.apiResponse}</p>
          <p>Socket timestamp: {this.state.timestamp}</p>
          <button onClick={() => this.toggle()}>Toggle state: {this.state.toggle}</button>

          <p>Welcome to Tic-Tac-Toe!</p>
          <div >
            {connected}
          </div>
          {/* <p>Your game id is: {this.state.gameId}</p> */}
        </div>

    );
  }
} // end Game

