      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({gameId:this.state.joinGameId})
    };

    const response = await fetch('/checkGameId',postOptions);
    const body = await response.json();

    if (response.status !== 200) throw Error(body.message);

    return body.isValidGame;

  }

  componentDidMount() {   

  }

  disconnect() {
    this.setState({
      gameId: '',
      joinGameId: '',
      player:'X',
      squares: Array(9).fill(null),
    });
    socket.emit('disconnect-opponent',this.state.gameId);
  }

//onMove={() => this.makeMove()}
  render() {
    let connected;
    if (this.state.gameId!=='') {
      connected = <div>
        <Board gameId={this.state.gameId} player={this.state.player} joinGameId={this.state.joinGameId} onDisconnece={this.disconnect} />

      </div>;
    }
    else {
      connected = <div>
        <p>Create or join a room to play!</p>
        <button onClick={() => this.createRoom()}>Create Room</button>
        <p> or </p>
        <button onClick={() => this.joinRoom()}>Join Room</button>
          <input type="text" placeholder="Game id" value={this.state.joinGameId} onChange={this.handleChange} />
      </div>;
    }
    return (
        <div className="Game">          
          <p>Welcome to Tic-Tac-Toe!</p>
          <div >
            {connected}
          </div>
          {/* <p>Your game id is: {this.state.gameId}</p> */}
        </div>

    );
  }
} // end Game

