import logo from './logo.svg';
import './App.css';
import React from 'react'
import openSocket from "socket.io-client";
const socket = openSocket();

function determineWinner(boardState) {
  // will come in ['X','O',null,null....]
  // win states: 3 same horizontal, 3 same vertical, 3 same diagonal

  // 0 1 2
  // 3 4 5
  // 6 7 8

  const winStates = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];

  for (let i = 0; i < winStates.length; i++) {
    const [a,b,c] = winStates[i];
    if (boardState[a] &&boardState[a] === boardState[b] && boardState[a] === boardState[c]) {
      return boardState[a];
    }
  }
  return null;
}

function subToTimer(cb) {
  socket.on('timer', timestamp => cb(null,timestamp));
  socket.emit('subscribeToTimer',1000);
}

function subToToggle(tog,cb) {
  socket.on('toggleButton', (gameId,toggle) => {
    console.log(`toggle return`);
    cb(null,gameId,toggle)
  });
  // socket.emit('toggle', tog);
}

function subToMove(cb) {
  socket.on('move-made', (gameId,player,move) => {
    cb(null,gameId,player,move);
  });
}

function Square(props) {
  return (
      <button className="square" onClick={props.onClick}>
        {props.value}
      </button>
  );
}

class Board extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      squares: Array(9).fill(null),
      player:'X',
      winner:null,
      gameId: this.props.gameId
    };


  }


  componentDidMount() {
    subToMove((err,gameId,player,move) => {
      if (gameId === this.state.gameId) {
        const squares = this.state.squares.slice();
        squares[move] = player;
        const winner = determineWinner(squares);
        this.setState({
          squares: squares,
          player: (this.state.player==='X') ? 'O' : 'X',
          winner: winner,
        });
      }
    });


  }

  handleClick(i) {
      const squares = this.state.squares.slice();
      if (determineWinner(squares) || squares[i]) {
        return;
      }
      socket.emit('make-move',this.state.gameId,this.state.player, i);

      squares[i] = this.state.player;
      const winner = determineWinner(squares);
      this.setState({
        squares: squares,
        player: (this.state.player==='X') ? 'O' : 'X',
        winner: winner
      });
  }

  renderSquare(i) {
    return <Square 
      value={this.state.squares[i]}
      onClick={() => this.handleClick(i,this.props.player)}
      />;
  }

  resetGame() {
    this.setState({
      squares: Array(9).fill(null),
      player: 'X',
      winner:null,
    });
  }

  render() {
    const winner = this.state.winner;
    let status;
    if (winner) {
      status = `Player ${winner} wins!`;
    }
    else {
      status = `Player ${this.state.player}'s turn`;
    }
    return (
      <div className="board">
        <p>{status}</p>
        <div className="board board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className="board board-row">
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className="board board-row">
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>

        <button className="reset-button" onClick={() => this.resetGame() }>Reset Game</button>
      </div>
    );
  }
}

class Game extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      apiResponse:'',
      socketResponse:'',
      gameId: '',
      timestamp: 'none',
      toggle: 'False',


    };
  }

  handleChange(event) {
    this.setState({gameId: event.target.value});
  }

  toggle() {
    console.log(`toggle func`);
    // socket.on('toggleButton', () => {
    //   this.setState({toggle: (this.state.toggle === 'False') ? 'True' : 'False'});
    // });
    const tog = this.state.toggle;
    socket.emit('toggle',this.state.gameId,tog);
  }

  callAPI = async () => {
    const response = await fetch('/b');
    const body = await response.json();

    if (response.status !== 200) throw Error(body.message);
    console.log(`api got: ${body.message}`);

    return body;
  }

  getGameId = async () => {
    // asks the server to generate a unique game id

    const postOptions = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({msg:'h'})
    };

    const response = await fetch('/gen',postOptions);
    const body = await response.json();

    if (response.status !== 200) throw Error(body.message);

    return body;

  }

  componentDidMount() {
    this.callAPI()
      // .then(res => console.log(res.express))
      .then(res => this.setState({apiResponse: res.express}))
      .catch(err => console.log(err));

    this.getGameId()
    .then(res => this.setState({gameId: res.id}))
    .catch(err => console.log(err));

    subToTimer((err,timestamp) => this.setState({timestamp:timestamp}));
    subToToggle(this.state.toggle,(err,gameId,toggle) => {
      if (gameId===this.state.gameId) {
        console.log(toggle);
        this.setState({toggle: (toggle==='False') ? 'True' : 'False'});
      }
    });

    this.handleChange = this.handleChange.bind(this);
  }



  render() {
    return (
        <div className="Game">
          <p>Api Response: {this.state.apiResponse}</p>
          <p>Socket timestamp: {this.state.timestamp}</p>
          <button onClick={() => this.toggle()}>Toggle state: {this.state.toggle}</button>

          <p>Welcome to Tic-Tac-Toe!</p>
          <div >
            <Board 
            gameId={this.state.gameId}/>
          </div>
          <p>Game Id:</p>
          <input type="text" value={this.state.gameId} onChange={this.handleChange} />
          {/* <p>Your game id is: {this.state.gameId}</p> */}
        </div>

    );
  }
}

export default Game;

